@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    /* å¥åº·ç»¿è‰²ä¸»é¢˜ */
    --background: 120 20% 98%;
    --foreground: 140 25% 15%;

    --card: 0 0% 100%;
    --card-foreground: 140 25% 15%;

    --popover: 0 0% 100%;
    --popover-foreground: 140 25% 15%;

    --primary: 145 65% 42%;
    --primary-foreground: 0 0% 100%;

    --secondary: 145 30% 94%;
    --secondary-foreground: 145 50% 25%;

    --muted: 140 15% 94%;
    --muted-foreground: 140 10% 45%;

    --accent: 85 60% 50%;
    --accent-foreground: 0 0% 100%;

    --destructive: 0 84% 60%;
    --destructive-foreground: 0 0% 100%;

    --border: 140 20% 88%;
    --input: 140 20% 88%;
    --ring: 145 65% 42%;

    --radius: 1rem;

    /* è‡ªå®šä¹‰æ¸å˜ */
    --gradient-primary: linear-gradient(135deg, hsl(145, 65%, 42%), hsl(85, 60%, 50%));
    --gradient-card: linear-gradient(180deg, hsl(0, 0%, 100%), hsl(120, 20%, 97%));
    --gradient-hero: linear-gradient(135deg, hsl(145, 65%, 95%), hsl(85, 60%, 95%));

    /* é˜´å½± */
    --shadow-soft: 0 4px 20px -4px hsl(145 65% 42% / 0.15);
    --shadow-card: 0 8px 32px -8px hsl(140 25% 15% / 0.1);
    --shadow-glow: 0 0 40px hsl(145 65% 42% / 0.2);
  }

  .dark {
    --background: 140 20% 8%;
    --foreground: 120 15% 95%;

    --card: 140 20% 12%;
    --card-foreground: 120 15% 95%;

    --popover: 140 20% 12%;
    --popover-foreground: 120 15% 95%;

    --primary: 145 65% 50%;
    --primary-foreground: 140 30% 8%;

    --secondary: 140 20% 18%;
    --secondary-foreground: 145 40% 70%;

    --muted: 140 15% 20%;
    --muted-foreground: 140 10% 60%;

    --accent: 85 60% 55%;
    --accent-foreground: 140 30% 8%;

    --destructive: 0 70% 50%;
    --destructive-foreground: 0 0% 100%;

    --border: 140 15% 22%;
    --input: 140 15% 22%;
    --ring: 145 65% 50%;

    --gradient-primary: linear-gradient(135deg, hsl(145, 65%, 50%), hsl(85, 60%, 55%));
    --gradient-card: linear-gradient(180deg, hsl(140, 20%, 12%), hsl(140, 20%, 10%));
    --gradient-hero: linear-gradient(135deg, hsl(145, 30%, 15%), hsl(85, 30%, 15%));

    --shadow-soft: 0 4px 20px -4px hsl(145 65% 50% / 0.2);
    --shadow-card: 0 8px 32px -8px hsl(0 0% 0% / 0.3);
    --shadow-glow: 0 0 40px hsl(145 65% 50% / 0.15);
  }
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground antialiased;
  }
}

@layer components {
  .gradient-primary {
    background: var(--gradient-primary);
  }

  .gradient-card {
    background: var(--gradient-card);
  }

  .gradient-hero {
    background: var(--gradient-hero);
  }

  .shadow-soft {
    box-shadow: var(--shadow-soft);
  }

  .shadow-card {
    box-shadow: var(--shadow-card);
  }

  .shadow-glow {
    box-shadow: var(--shadow-glow);
  }

  .upload-zone {
    @apply relative overflow-hidden rounded-2xl border-2 border-dashed border-primary/30 bg-secondary/30 p-8 transition-all duration-300;
  }

  .upload-zone:hover {
    @apply border-primary/60 bg-secondary/50 shadow-soft;
  }

  .upload-zone.dragging {
    @apply border-primary bg-primary/10 shadow-glow;
  }

  .result-card {
    @apply rounded-2xl bg-card p-6 shadow-card transition-all duration-300 hover:shadow-soft;
  }

  .nutrient-bar {
    @apply h-2 rounded-full bg-secondary overflow-hidden;
  }

  .nutrient-fill {
    @apply h-full rounded-full transition-all duration-700 ease-out;
  }
}

@layer utilities {
  .animate-fade-in {
    animation: fadeIn 0.5s ease-out forwards;
  }

  .animate-slide-up {
    animation: slideUp 0.5s ease-out forwards;
  }

  .animate-pulse-soft {
    animation: pulseSoft 2s ease-in-out infinite;
  }

  .animate-scan {
    animation: scan 2s ease-in-out infinite;
  }
}

@keyframes fadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

@keyframes slideUp {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes pulseSoft {
  0%, 100% {
    opacity: 1;
  }
  50% {
    opacity: 0.6;
  }
}

@keyframes scan {
  0%, 100% {
    transform: translateY(-100%);
  }
  50% {
    transform: translateY(100%);
  }
}

import { useCallback, useState } from "react";
import { Upload, Camera, X } from "lucide-react";

interface ImageUploaderProps {
  onImageSelect: (file: File) => void;
  imagePreview: string | null;
  onClear: () => void;
  isAnalyzing: boolean;
}

export function ImageUploader({
  onImageSelect,
  imagePreview,
  onClear,
  isAnalyzing,
}: ImageUploaderProps) {
  const [isDragging, setIsDragging] = useState(false);

  const handleDragOver = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(true);
  }, []);

  const handleDragLeave = useCallback((e: React.DragEvent) => {
    e.preventDefault();
    setIsDragging(false);
  }, []);

  const handleDrop = useCallback(
    (e: React.DragEvent) => {
      e.preventDefault();
      setIsDragging(false);
      const file = e.dataTransfer.files[0];
      if (file && file.type.startsWith("image/")) {
        onImageSelect(file);
      }
    },
    [onImageSelect]
  );

  const handleFileSelect = useCallback(
    (e: React.ChangeEvent<HTMLInputElement>) => {
      const file = e.target.files?.[0];
      if (file) {
        onImageSelect(file);
      }
    },
    [onImageSelect]
  );

  if (imagePreview) {
    return (
      <div className="relative overflow-hidden rounded-2xl shadow-card">
        <img
          src={imagePreview}
          alt="é£Ÿç‰©å›¾ç‰‡"
          className="w-full h-64 md:h-80 object-cover"
        />
        {isAnalyzing && (
          <div className="absolute inset-0 bg-foreground/30 flex items-center justify-center">
            <div className="absolute inset-0 overflow-hidden">
              <div className="absolute inset-x-0 h-1 gradient-primary animate-scan" />
            </div>
            <div className="bg-card/90 backdrop-blur-sm rounded-xl px-6 py-3 shadow-soft">
              <div className="flex items-center gap-3">
                <div className="w-5 h-5 border-2 border-primary border-t-transparent rounded-full animate-spin" />
                <span className="text-foreground font-medium">AI åˆ†æä¸­...</span>
              </div>
            </div>
          </div>
        )}
        {!isAnalyzing && (
          <button
            onClick={onClear}
            className="absolute top-3 right-3 p-2 bg-card/80 backdrop-blur-sm rounded-full shadow-soft hover:bg-card transition-colors"
          >
            <X className="w-5 h-5 text-foreground" />
          </button>
        )}
      </div>
    );
  }

  return (
    <div
      className={`upload-zone ${isDragging ? "dragging" : ""}`}
      onDragOver={handleDragOver}
      onDragLeave={handleDragLeave}
      onDrop={handleDrop}
    >
      <input
        type="file"
        accept="image/*"
        onChange={handleFileSelect}
        className="absolute inset-0 w-full h-full opacity-0 cursor-pointer"
      />
      <div className="flex flex-col items-center gap-4 py-8">
        <div className="w-20 h-20 rounded-full gradient-primary flex items-center justify-center shadow-glow">
          <Upload className="w-10 h-10 text-primary-foreground" />
        </div>
        <div className="text-center">
          <p className="text-lg font-semibold text-foreground mb-1">
            ä¸Šä¼ é£Ÿç‰©å›¾ç‰‡
          </p>
          <p className="text-muted-foreground text-sm">
            æ‹–æ‹½å›¾ç‰‡åˆ°è¿™é‡Œï¼Œæˆ–ç‚¹å‡»é€‰æ‹©å›¾ç‰‡
          </p>
        </div>
        <div className="flex items-center gap-2 text-xs text-muted-foreground">
          <Camera className="w-4 h-4" />
          <span>æ”¯æŒ JPG, PNG, WebP æ ¼å¼</span>
        </div>
      </div>
    </div>
  );
}

import { Flame, Beef, Wheat, Droplets } from "lucide-react";

export interface FoodAnalysis {
  name: string;
  calories: number;
  protein: number;
  carbs: number;
  fat: number;
  fiber?: number;
  confidence: number;
  tips?: string;
}

interface NutritionResultProps {
  result: FoodAnalysis;
}

const NutrientBar = ({
  value,
  max,
  color,
}: {
  value: number;
  max: number;
  color: string;
}) => {
  const percentage = Math.min((value / max) * 100, 100);
  return (
    <div className="nutrient-bar">
      <div
        className={`nutrient-fill ${color}`}
        style={{ width: `${percentage}%` }}
      />
    </div>
  );
};

