      const analysis = await analyzeFood(imagePreview);
      setResult(analysis);
    } catch (err) {
      setError("åˆ†æå¤±è´¥ï¼Œè¯·é‡è¯•");
      console.error(err);
    } finally {
      setIsAnalyzing(false);
    }
  }, [imagePreview]);

  return (
    <div className="min-h-screen gradient-hero">
