import { FoodAnalysis } from "../components/NutritionResult";

const AI_GATEWAY_URL = (window as any).ENV_AI_GATEWAY_URL || "";

export async function analyzeFood(imageDataUrl: string): Promise<FoodAnalysis> {
  const base64Data = imageDataUrl.split(",")[1];

  const response = await fetch(AI_GATEWAY_URL, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify({
      model: "claude-sonnet-4-20250514",
      max_tokens: 1024,
      messages: [
        {
          role: "user",
          content: [
            {
              type: "image",
              source: {
                type: "base64",
                media_type: "image/jpeg",
                data: base64Data,
              },
            },
            {
              type: "text",
              text: `è¯·åˆ†æè¿™å¼ å›¾ç‰‡ä¸­çš„é£Ÿç‰©ï¼Œå¹¶æä¾›ä»¥ä¸‹JSONæ ¼å¼çš„è¥å…»ä¿¡æ¯ï¼ˆåªè¿”å›JSONï¼Œä¸è¦å…¶ä»–æ–‡å­—ï¼‰ï¼š

{
  "name": "é£Ÿç‰©åç§°ï¼ˆä¸­æ–‡ï¼‰",
  "calories": ä¼°ç®—å¡è·¯é‡Œæ•°å€¼ï¼ˆæ•°å­—ï¼Œå•ä½ï¼šåƒå¡ï¼‰,
  "protein": è›‹ç™½è´¨å…‹æ•°ï¼ˆæ•°å­—ï¼‰,
  "carbs": ç¢³æ°´åŒ–åˆç‰©å…‹æ•°ï¼ˆæ•°å­—ï¼‰,
  "fat": è„‚è‚ªå…‹æ•°ï¼ˆæ•°å­—ï¼‰,
  "confidence": ä½ å¯¹è¿™ä¸ªåˆ†æçš„ç½®ä¿¡åº¦ï¼ˆ1-100çš„æ•°å­—ï¼‰,
  "tips": "ä¸€å¥ç®€çŸ­çš„å¥åº·é¥®é£Ÿå»ºè®®ï¼ˆä¸­æ–‡ï¼‰"
}

æ³¨æ„ï¼š
1. å¦‚æœå›¾ç‰‡ä¸­æœ‰å¤šç§é£Ÿç‰©ï¼Œè¯·ä¼°ç®—æ•´ä½“çš„è¥å…»æˆåˆ†æ€»å’Œ
2. æ•°å€¼åº”è¯¥æ˜¯åˆç†çš„ä¼°ç®—å€¼
3. å¦‚æœæ— æ³•è¯†åˆ«é£Ÿç‰©ï¼Œnameè®¾ä¸º"æ— æ³•è¯†åˆ«"ï¼Œå…¶ä»–æ•°å€¼è®¾ä¸º0ï¼Œconfidenceè®¾ä¸º0`,
            },
          ],
        },
      ],
    }),
  });

  if (!response.ok) {
    throw new Error("AI åˆ†æè¯·æ±‚å¤±è´¥");
  }

  const data = await response.json();
  const content = data.content?.[0]?.text || "";

  // æå– JSON
  const jsonMatch = content.match(/\{[\s\S]*\}/);
  if (!jsonMatch) {
    throw new Error("æ— æ³•è§£æ AI å“åº”");
  }

  const result = JSON.parse(jsonMatch[0]) as FoodAnalysis;
  return result;
}
